def longestPallindrome(s):
    word=""
    pallindromedict={}
    for num,i in enumerate(s):
        word=i
        for j in s[s.index(i,num,len(s))+1:]:
            word+=j
            if word==word[::-1]:
                pallindromedict[word]=len(word)
    return max(zip(pallindromedict.values(), pallindromedict.keys()))[1]            
    #return max(zip(pallindromedict.values(), pallindromedict.keys()))[1]
def longestPallindrome2(s):
    pallindromedict={}
    i=0
    while i<len(s):
        j=i+1
        while j<=len(s):
            if s[i:j]==s[i:j][::-1]:
                pallindromedict[s[i:j]]=len(s[i:j])
            j+=1
        i+=1
    return max(zip(pallindromedict.values(), pallindromedict.keys()))[1]
